import React from 'react';
import { Link } from 'react-router-dom';
import { FOOTER_LINKS } from '../data/siteData';
import styles from './Footer.module.css';

export default function Footer() {
  const companyLinks = {
    'About Us': '/company#about',
    'Case Studies': '/company#case-studies',
    'Careers': '/company#careers',
    'Blog': '/company#blog',
    'Partners': '/company#partners',
  };

  return (
    <footer className={styles.footer}>
      <div className={styles.container}>
        <div className={styles.top}>
          <div className={styles.brand}>
            <div className={styles.logo}>
              Creatix<span>Hub</span>
            </div>
            <p className={styles.brandDesc}>
              Future-forward IT solutions for businesses that want to move faster,
              scale smarter, and operate with confidence in the modern digital era.
            </p>
            <div className={styles.socials}>
              {['in', 'ð•', 'âŒ¥', 'â–¶'].map((icon, i) => (
                <a key={i} href="#" className={styles.social} title={['LinkedIn', 'Twitter', 'GitHub', 'YouTube'][i]}>
                  {icon}
                </a>
              ))}
            </div>
          </div>

          {Object.entries(FOOTER_LINKS).map(([heading, links]) => (
            <div key={heading} className={styles.col}>
              <h5 className={styles.colTitle}>{heading}</h5>
              {links.map((link) => {
                if (heading === 'Company') {
                  return (
                    <Link key={link} to={companyLinks[link]} className={styles.link}>
                      {link}
                    </Link>
                  );
                } else if (heading === 'Contact') {
                  const isEmail = link.includes('@');
                  const isPhone = /^[0-9()+\s-]+$/.test(link);
                  let href = '#';
                  if (isEmail) {
                    href = `mailto:${link}`;
                  } else if (isPhone) {
                    href = `tel:${link}`;
                  }
                  return (
                    <a key={link} href={href} className={styles.link}>
                      {link}
                    </a>
                  );
                } else {
                  return (
                    <a key={link} href="#" className={styles.link}>
                      {link}
                    </a>
                  );
                }
              })}
            </div>
          ))}
        </div>

        <div className={styles.bottom}>
          <span>Â© 2026 CreatixHub Technologies Pvt. Ltd. All rights reserved.</span>
          <div className={styles.bottomLinks}>
            <a href="#">Privacy Policy</a>
            <a href="#">Terms of Service</a>
            <a href="#">Cookie Policy</a>
          </div>
        </div>
      </div>
    </footer>
  );
}
